package org.greenfroyo.androidmvp_bind.app._core.delegation;import android.content.Context;import android.support.design.widget.AppBarLayout;import android.support.design.widget.CoordinatorLayout;import android.support.v4.view.LayoutInflaterCompat;import android.support.v7.app.AppCompatActivity;import android.support.v7.widget.Toolbar;import android.view.LayoutInflater;import org.greenfroyo.androidmvp_bind.R;/** * Created by junius.ang on 8/25/2016. */public class CoreDelegateDependency {    protected LayoutInflater mLayoutInflater;    protected CoordinatorLayout mCoordinatorLayout;    protected AppBarLayout mAppBarLayout;    public CoreDelegateDependency(LayoutInflater layoutInflater, CoordinatorLayout coordinatorLayout, AppBarLayout appBarLayout) {        this.mLayoutInflater = layoutInflater;        this.mCoordinatorLayout = coordinatorLayout;        this.mAppBarLayout = appBarLayout;    }    public AppBarLayout getAppBarLayout() {        return mAppBarLayout;    }    public void setAppBarLayout(AppBarLayout mAppBarLayout) {        this.mAppBarLayout = mAppBarLayout;    }    public CoordinatorLayout getCoordinatorLayout() {        return mCoordinatorLayout;    }    public void setCoordinatorLayout(CoordinatorLayout mCoordinatorLayout) {        this.mCoordinatorLayout = mCoordinatorLayout;    }    public LayoutInflater getLayoutInflater() {        return mLayoutInflater;    }    public void setLayoutInflater(LayoutInflater mLayoutInflater) {        this.mLayoutInflater = mLayoutInflater;    }}